package com.nccevent.horizontalscrollingecomproductsdemo.Items;public class ProductsItem {    String productName, productPrice, spec,farmerName,qty,stock;    String productImageUrl;    public String getProductImageUrl() {        return productImageUrl;    }    public void setProductImageUrl(String productImageUrl) {        this.productImageUrl = productImageUrl;    }    public String getProductName() {        return productName;    }    public void setProductName(String productName) {        this.productName = productName;    }    public String getProductPrice() {        return productPrice;    }    public void setProductPrice(String productPrice) {        this.productPrice = productPrice;    }    public String getSpec() {        return spec;    }    public void setSpec(String spec) {        this.spec = spec;    }    public String getFarmerName() {        return farmerName;    }    public void setFarmerName(String farmerName) {        this.farmerName = farmerName;    }    public String getQty() {        return qty;    }    public void setQty(String qty) {        this.qty = qty;    }    public String getStock() {        return stock;    }    public void setStock(String stock) {        this.stock = stock;    }}